import React from 'react';
import AceEditor from 'react-ace';
import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-github';

const CodeEditor = ({ code, setCode, input, setInput }) => {
  return (
    <div>
      <AceEditor
        mode="python"
        theme="github"
        name="codeEditor"
        onChange={setCode}
        value={code}
        editorProps={{ $blockScrolling: true }}
        width="60%"
        height="200px"
        style={{ border: '1px solid #ccc', borderRadius: '5px',  boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',}}
      />
      <textarea
        placeholder="Введите входные данные"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        style={{ width: '60%', height: '100px', marginTop: '10px', resize: 'none', borderRadius: '5px',}}
      />
    </div>
  );
}

export default CodeEditor;
