import React, { useState } from 'react';
import '../styles/AddLesson.css';

function AddLesson() {
  const [title, setTitle] = useState('');
  const [sections, setSections] = useState([{ subtitle: '', content: '' }]);
  
  const [showQuiz, setShowQuiz] = useState(false);
  const [quizzes, setQuizzes] = useState([]);

  const [showCodeExecutor, setShowCodeExecutor] = useState(false);
  const [codeExecutor, setCodeExecutor] = useState({
    file: '',
    taskDescription: '',
    taskInputFormat: '',
    taskOutputFormat: '',
    taskExample: { input: '', output: '' }
  });
  const [quizCount, setQuizCount] = useState(0); // Переменная для хранения количества Quiz

  const handleSectionChange = (index, event) => {
    const newSections = [...sections];
    newSections[index][event.target.name] = event.target.value;
    setSections(newSections);
  };

  const handleQuizChange = (index, event) => {
    const newQuizzes = [...quizzes];
    if (event.target.name === 'options') {
      const optionIndex = parseInt(event.target.dataset.index);
      newQuizzes[index].options[optionIndex] = event.target.value;
    } else {
      newQuizzes[index][event.target.name] = event.target.value;
    }
    setQuizzes(newQuizzes);
  };

  const handleCodeExecutorChange = (event) => {
    const { name, value } = event.target;
    setCodeExecutor((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleTaskExampleChange = (event) => {
    const { name, value } = event.target;
    setCodeExecutor((prev) => ({
      ...prev,
      taskExample: {
        ...prev.taskExample,
        [name]: value,
      },
    }));
  };

  const addSection = () => {
    setSections([...sections, { subtitle: '', content: '' }]);
  };

  const removeSection = (index) => {
    const newSections = [...sections];
    newSections.splice(index, 1);
    setSections(newSections);
  };

  const addQuiz = () => {
    setQuizzes([...quizzes, { question: '', options: ['', ''], answer: '' }]);
    setQuizCount((prevCount) => prevCount + 1);
    setShowQuiz(true); // Показываем Quiz при добавлении первого элемента
  };

  const addOption = (quizIndex) => {
    const newQuizzes = [...quizzes];
    newQuizzes[quizIndex].options.push('');
    setQuizzes(newQuizzes);
  };

  const removeOption = (quizIndex, optionIndex) => {
    const newQuizzes = [...quizzes];
    newQuizzes[quizIndex].options.splice(optionIndex, 1);
    setQuizzes(newQuizzes);
  };

  const removeQuiz = () => {
    const newQuizzes = [...quizzes];
    newQuizzes.pop(); // Удаляем последний Quiz
    setQuizzes(newQuizzes);
    setQuizCount((prevCount) => prevCount - 1);
    if (quizCount === 1) {
      setShowQuiz(false); // Скрываем Quiz при удалении единственного элемента
    }
  };

  const addCodeExecutor = () => {
    setShowCodeExecutor(true);
  };

  const removeCodeExecutor = () => {
    setCodeExecutor({
      file: '',
      taskDescription: '',
      taskInputFormat: '',
      taskOutputFormat: '',
      taskExample: { input: '', output: '' }
    });
    setShowCodeExecutor(false); // Скрываем Code Executor при удалении
  };

  const addContentItem = (type) => {
    setSections(prevSections => [
      ...prevSections,
      { type, value: '' } // Добавляем элемент с указанным типом и пустым значением
    ]);
  };

  // Функция для удаления элемента контента по индексу
  const removeContentItem = (index) => {
    setSections(prevSections => {
      const updatedSections = [...prevSections];
      updatedSections.splice(index, 1);
      return updatedSections;
    });
  };

  // Функция для обновления значения элемента контента
  const handleContentChange = (index, value) => {
    setSections(prevSections => {
      const updatedSections = [...prevSections];
      updatedSections[index].value = value;
      return updatedSections;
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const lesson = { title, sections, quizzes, codeExecutor };

    const response = await fetch('http://localhost:4000/api/lessons', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(lesson),
    });

    console.log(JSON.stringify(lesson))
    if (response.ok) {
      // Обработка успешного добавления урока
      alert('Lesson added successfully!');
    } else {
      // Обработка ошибки
      alert('Error adding lesson.');
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        {/* {sections.map((section, index) => (
          <div key={index} className="form-group">
            <label htmlFor={`subtitle-${index}`}>Subtitle:</label>
            <input
              type="text"
              id={`subtitle-${index}`}
              name="subtitle"
              value={section.subtitle}
              onChange={(e) => handleSectionChange(index, e)}
              required
            />
            <label htmlFor={`content-${index}`}>Content:</label>
            <textarea
              id={`content-${index}`}
              name="content"
              value={section.content}
              onChange={(e) => handleSectionChange(index, e)}
              required
            />
            {index !== 0 && (
              <button type="button" onClick={() => removeSection(index)}>
                Remove Section
              </button>
            )}
          </div>
        ))} */}

    {sections.map((section, index) => (
        <div key={index} className="form-group">
          {section.type === 'text' && (
            <div>
              <label>Текст:</label>
              <textarea
                value={section.value}
                onChange={(e) => handleContentChange(index, e.target.value)}
                required
              />
            </div>
          )}

          {section.type === 'list' && (
            <div>
              <label>Список:</label>
              <ul>
                <li>
                  <input type="text" placeholder="Элемент 1" />
                </li>
                <li>
                  <input type="text" placeholder="Элемент 2" />
                </li>
                {/* Добавить возможность добавления и удаления элементов списка */}
              </ul>
            </div>
          )}

          {section.type === 'code' && (
            <div>
              <label>Code Block:</label>
              <textarea
                value={section.value}
                onChange={(e) => handleContentChange(index, e.target.value)}
                required
              />
            </div>
          )}

          <button type="button" onClick={() => removeContentItem(index)}>
            Remove Content
          </button>
        </div>
    ))}

    <div>
        <button type="button" onClick={() => addContentItem('text')}>Добавить текст</button>
        <button type="button" onClick={() => addContentItem('list')}>Добавить список</button>
        <button type="button" onClick={() => addContentItem('code')}>Добавить код</button>
      </div>

        <button type="button" className="add-section-button" onClick={addSection}>
          Add Section
        </button>

        {!showQuiz && (
          <button 
            type="button" 
            className="add-section-button" 
            onClick={addQuiz}
        >
            Add Quiz
          </button>
        )}

        {showQuiz && (
          <>
            {quizzes.map((quiz, index) => (
              <div key={index} className="form-group">
                <label htmlFor={`question-${index}`}>Question:</label>
                <input
                  type="text"
                  id={`question-${index}`}
                  name="question"
                  value={quiz.question}
                  onChange={(e) => handleQuizChange(index, e)}
                  required
                />
                {quiz.options.map((option, optionIndex) => (
                  <div key={optionIndex} className="option-group">
                    <label htmlFor={`option-${index}-${optionIndex}`}>Option {optionIndex + 1}:</label>
                    <input
                      type="text"
                      id={`option-${index}-${optionIndex}`}
                      name="options"
                      data-index={optionIndex}
                      value={option}
                      onChange={(e) => handleQuizChange(index, e)}
                      required
                    />
                    <button type="button" onClick={() => removeOption(index, optionIndex)}>
                      Remove Option
                    </button>
                  </div>
                ))}
                {index === quizzes.length - 1 && (
                  <button type="button" onClick={() => addOption(index)}>
                    Add Option
                  </button>
                )}
                {index === quizzes.length - 1 && (
                  <button type="button" onClick={removeQuiz}>
                    Remove Quiz
                  </button>
                )}
                <label htmlFor={`answer-${index}`}>Answer:</label>
                <input
                  type="text"
                  id={`answer-${index}`}
                  name="answer"
                  value={quiz.answer}
                  onChange={(e) => handleQuizChange(index, e)}
                  required
                />
              </div>
            ))}
            <button type="button" className="add-section-button" onClick={addQuiz}>
              Add Another Quiz
            </button>
          </>
        )}

        {!showCodeExecutor && (
          <button
            type="button"
            className="add-section-button"
            onClick={addCodeExecutor}
          >
            Add Code Executor
          </button>
        )}

        {showCodeExecutor && (
          <div className="form-group">
            <label htmlFor="file">Code Executor File:</label>
            <input
              type="text"
              id="file"
              name="file"
              value={codeExecutor.file}
              onChange={handleCodeExecutorChange}
              required
            />
            <label htmlFor="taskDescription">Task Description:</label>
            <input
              type="text"
              id="taskDescription"
              name="taskDescription"
              value={codeExecutor.taskDescription}
              onChange={handleCodeExecutorChange}
              required
            />
            <label htmlFor="taskInputFormat">Task Input Format:</label>
            <input
              type="text"
              id="taskInputFormat"
              name="taskInputFormat"
              value={codeExecutor.taskInputFormat}
              onChange={handleCodeExecutorChange}
              required
            />
            <label htmlFor="taskOutputFormat">Task Output Format:</label>
            <input
              type="text"
              id="taskOutputFormat"
              name="taskOutputFormat"
              value={codeExecutor.taskOutputFormat}
              onChange={handleCodeExecutorChange}
              required
            />
            <label htmlFor="taskExampleInput">Task Example Input:</label>
            <input
              type="text"
              id="taskExampleInput"
              name="input"
              value={codeExecutor.taskExample.input}
              onChange={handleTaskExampleChange}
              required
            />
            <label htmlFor="taskExampleOutput">Task Example Output:</label>
            <input
              type="text"
              id="taskExampleOutput"
              name="output"
              value={codeExecutor.taskExample.output}
              onChange={handleTaskExampleChange}
              required
            />
            <button type="button" onClick={removeCodeExecutor}>
              Remove Code Executor
            </button>
          </div>
        )}

        <div className="button-container">
          <button type="submit">Add Lesson</button>
        </div>
      </form>
    </div>
  );
}

export default AddLesson;
