import React, { useState } from 'react';
import axios from 'axios';
import CodeEditor from './CodeEditor';

const CodeExecutor = (file) => {
  const [code, setCode] = useState('');
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const [error, setError] = useState('');
  const [resultMessage, setResultMessage] = useState('');
  const [input_data, setInputData] = useState('');
  const [expectedOutput, setExpectedOutput] = useState('');



  const executeCode = async (action) => {
    try {
      const response = await axios.post('http://localhost:5000/execute', { code, input, file, action });
      setOutput(response.data.output);
      setError(response.data.error);
      setResultMessage(response.data.resultMessage);
      setInputData(response.data.inputData);
      setExpectedOutput(response.data.expectedOutput);
    } catch (err) {
      console.log(err)
      setError('Error executing code.');
    }
  };

  return (
    <div>
      <CodeEditor code={code} setCode={setCode} input={input} setInput={setInput} />
      {file !== "0" && 
      <button onClick={() => executeCode('')} style={{ marginTop: '20px' }}>Запустить код {file}</button> }
      <button onClick={() => executeCode('run')} style={{ marginTop: '20px', marginLeft: '20px' }}>Отправить на проверку</button>
      <div>
        {resultMessage &&
            <h2>{resultMessage}</h2>
        }
        {error && (
            <div>
              <h3>Ошибка:</h3>
              <pre>{error}</pre>
            </div>
        )}
        <h3>Входные данные:</h3>
        <pre>{input_data}</pre>
        <h3>Ваш вывод:</h3>
        <pre>{output}</pre>
        {expectedOutput && 
            <div>
              <h3>Ожидаемый вывод:</h3>
              <pre>{expectedOutput}</pre>
            </div>
        }
      </div>
    </div>
  );
}

export default CodeExecutor;
