import React, { useState } from 'react';
import axios from 'axios';
import CodeEditor from './CodeEditor';
import '../styles/CodeEditor.css';


const CodeExecutor = ({file}) => {
  const [code, setCode] = useState('');
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const [error, setError] = useState('');
  const [resultMessage, setResultMessage] = useState('');
  const [input_data, setInputData] = useState('');
  const [expectedOutput, setExpectedOutput] = useState('');

  const [isLoading, setIsLoading] = useState(false); // State to track loading state

  const executeCode = async (action) => {
    setIsLoading(true); // Set loading state to true when starting execution
    try {
      const response = await axios.post('http://localhost:5000/execute', { code, input, file, action });
      setOutput(response.data.output);
      setError(response.data.error);
      setResultMessage(response.data.resultMessage);
      setInputData(response.data.inputData);
      setExpectedOutput(response.data.expectedOutput);
    } catch (err) {
      console.log(err)
      setError('Error executing code.');
    } finally {
      setIsLoading(false); // Set loading state to false when request completes (whether success or error)
    }
  };

  // return (
  //   <div className="editor-container">
  //     <CodeEditor code={code} setCode={setCode} input={input} setInput={setInput} />
      
  //     <div className="editor-buttons">
  //       <button
  //         disabled={isLoading} 
  //         onClick={() => executeCode('')} 
  //         className={isLoading ? 'button-loading' : ''}>
  //       Запустить код</button>
  //       {file !== "0" && (
  //         <button 
  //           style={{ marginLeft: '20px' }}
  //           disabled={isLoading} 
  //           onClick={() => executeCode('run')} 
  //           className={isLoading ? 'button-loading' : ''}>
  //           Отправить на проверку
  //         </button>
  //       )}
  //     </div>

  //     <div className="editor-result">
  //       {resultMessage &&
  //         <h2 style = {{borderBottom: "2px solid #282c34"}} >{resultMessage}</h2>
  //       }

  //       {error && (
  //         <div>
  //           <h3>Ошибка:</h3>
  //           <pre>{error}</pre>
  //         </div>
  //       )}

  //       <h3>Входные данные:</h3>
  //       <pre>{input_data}</pre>

  //       <h3>Ваш вывод:</h3>
  //       <pre>{output}</pre>

  //       {expectedOutput && 
  //         <div>
  //           <h3>Ожидаемый вывод:</h3>
  //           <pre>{expectedOutput}</pre>
  //         </div>
  //       }
  //     </div>
  //   </div>
  return (
    <div className='code-executor'>
      <h2>{codeExecutor.taskDescription}</h2>
      <p><strong>Input Format:</strong> {codeExecutor.taskInputFormat}</p>
      <p><strong>Output Format:</strong> {codeExecutor.taskOutputFormat}</p>
      <div>
        <h3>Example:</h3>
        <pre>Input: {codeExecutor.taskExample.input}</pre>
        <pre>Output: {codeExecutor.taskExample.output}</pre>
      </div>
      <div>
        <h3>Tests:</h3>
        {codeExecutor.tests.map((test, index) => (
          <div key={index}>
            <pre>Input: {test.input}</pre>
            <pre>Expected Output: {test.expectedOutput}</pre>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CodeExecutor;
