import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Подключаем axios для HTTP запросов

const LessonsList = () => {
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    // Функция для загрузки данных с сервера
    const fetchLessons = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/lessons');
        setLessons(response.data); // Устанавливаем полученные уроки в состояние
      } catch (error) {
        console.error('Ошибка при загрузке уроков:', error);
      }
    };

    fetchLessons(); // Вызываем функцию загрузки данных при монтировании компонента
  }, []);

  return (
    <div>
      <h2>,,t,tt,t,Список уроков</h2>
      <ul>
        {lessons.map((lesson) => (
          <li key={lesson._id}>
            <h3>{lesson.title}</h3>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LessonsList;
