// src/components/Lesson.js
import React from 'react';

const Lesson = ({ lesson }) => {
  return (
    <div className="lesson">
      <h1>{lesson.title}</h1>
      {lesson.sections.map((section, index) => {
        switch (section.type) {
          case 'text':
            return <p key={index}>{section.content}</p>;
          case 'quiz':
            return (
              <div key={index}>
                <h2>Quiz</h2>
                {section.questions.map((question, qIndex) => (
                  <div key={qIndex}>
                    <p>{question.question}</p>
                    <ul>
                      {question.options.map((option, oIndex) => (
                        <li key={oIndex}>{option}</li>
                      ))}
                    </ul>
                  </div>
                ))}
              </div>
            );
          case 'codeExecutor':
            return (
              <div key={index}>
                <h2>Code Executor</h2>
                <p><strong>Assignment:</strong> {section.assignment}</p>
                <p><strong>Input Data:</strong> {section.inputData}</p>
                <p><strong>Output Data:</strong> {section.outputData}</p>
                <pre><code>{section.example}</code></pre>
              </div>
            );
          default:
            return null;
        }
      })}
    </div>
  );
};

export default Lesson;
