import React, { useState } from 'react';
import '../styles/Quiz.css';

const Quiz = () => {
  const questions = [
    {
      question: "Какой тип данных используется для хранения текста в Python?",
      options: ["int", "str", "list", "dict"],
      answer: "str"
    },
    {
      question: "Какой оператор используется для присваивания значений переменным?",
      options: ["=", "==", "===", "=>"],
      answer: "="
    },
    {
      question: "Какой из этих методов используется для добавления элемента в конец списка?",
      options: [".append()", ".add()", ".insert()", ".push()"],
      answer: ".append()"
    }
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const handleAnswerOptionClick = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].answer)
      setScore(score + 1);

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length)
      setCurrentQuestion(nextQuestion);
    else
      setShowResult(true);
  };

  return (
    <div className="quiz-container">
      {showResult ? (
      <div className="result-section">
        <h2>Тест пройден успешно!</h2>
        <p>Ваш результат: {score} из {questions.length}</p>
      </div>) : (
      <div className="question-section">
        <h2>Вопрос {currentQuestion + 1} из {questions.length}</h2>
        <p>{questions[currentQuestion].question}</p>
        <div className="options-section">
          {questions[currentQuestion].options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnswerOptionClick(option)}>
              {option}
            </button>
          ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Quiz;
