import React, { useState } from 'react';
import '../styles/AddLesson.css';

function AddLesson() {
  const [title, setTitle] = useState('');
  const [sections, setSections] = useState([{ subtitle: '', content: '' }]);
  const [quizzes, setQuizzes] = useState([{ question: '', options: ['', ''], answer: '' }]);
  const [codeExecutor, setCodeExecutor] = useState({
    file: '',
    taskDescription: '',
    taskInputFormat: '',
    taskOutputFormat: '',
    taskExample: { input: '', output: '' }
  });

  const handleSectionChange = (index, event) => {
    const newSections = [...sections];
    newSections[index][event.target.name] = event.target.value;
    setSections(newSections);
  };

  const handleQuizChange = (index, event) => {
    const newQuizzes = [...quizzes];
    if (event.target.name === 'options') {
      const optionIndex = parseInt(event.target.dataset.index);
      newQuizzes[index].options[optionIndex] = event.target.value;
    } else {
      newQuizzes[index][event.target.name] = event.target.value;
    }
    setQuizzes(newQuizzes);
  };

  const handleCodeExecutorChange = (event) => {
    const { name, value } = event.target;
    setCodeExecutor((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleTaskExampleChange = (event) => {
    const { name, value } = event.target;
    setCodeExecutor((prev) => ({
      ...prev,
      taskExample: {
        ...prev.taskExample,
        [name]: value,
      },
    }));
  };

  const addSection = () => {
    setSections([...sections, { subtitle: '', content: '' }]);
  };

  const addQuiz = () => {
    setQuizzes([...quizzes, { question: '', options: ['', ''], answer: '' }]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const lesson = { title, sections, quizzes, codeExecutor };

    const response = await fetch('/api/lessons', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(lesson),
    });

    if (response.ok) {
      // Обработка успешного добавления урока
      alert('Lesson added successfully!');
    } else {
      // Обработка ошибки
      alert('Error adding lesson.');
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        {sections.map((section, index) => (
          <div key={index} className="form-group">
            <label htmlFor={`subtitle-${index}`}>Subtitle:</label>
            <input
              type="text"
              id={`subtitle-${index}`}
              name="subtitle"
              value={section.subtitle}
              onChange={(e) => handleSectionChange(index, e)}
              required
            />
            <label htmlFor={`content-${index}`}>Content:</label>
            <textarea
              id={`content-${index}`}
              name="content"
              value={section.content}
              onChange={(e) => handleSectionChange(index, e)}
              required
            />
          </div>
        ))}
        <button type="button" className="add-section-button" onClick={addSection}>
          Add Section
        </button>

        {quizzes.map((quiz, index) => (
          <div key={index} className="form-group">
            <label htmlFor={`question-${index}`}>Question:</label>
            <input
              type="text"
              id={`question-${index}`}
              name="question"
              value={quiz.question}
              onChange={(e) => handleQuizChange(index, e)}
              required
            />
            {quiz.options.map((option, optionIndex) => (
              <div key={optionIndex}>
                <label htmlFor={`option-${index}-${optionIndex}`}>Option {optionIndex + 1}:</label>
                <input
                  type="text"
                  id={`option-${index}-${optionIndex}`}
                  name="options"
                  data-index={optionIndex}
                  value={option}
                  onChange={(e) => handleQuizChange(index, e)}
                  required
                />
              </div>
            ))}
            <label htmlFor={`answer-${index}`}>Answer:</label>
            <input
              type="text"
              id={`answer-${index}`}
              name="answer"
              value={quiz.answer}
              onChange={(e) => handleQuizChange(index, e)}
              required
            />
          </div>
        ))}
        <button type="button" className="add-section-button" onClick={addQuiz}>
          Add Quiz
        </button>

        <div className="form-group">
          <label htmlFor="file">Code Executor File:</label>
          <input
            type="text"
            id="file"
            name="file"
            value={codeExecutor.file}
            onChange={handleCodeExecutorChange}
            required
          />
          <label htmlFor="taskDescription">Task Description:</label>
          <input
            type="text"
            id="taskDescription"
            name="taskDescription"
            value={codeExecutor.taskDescription}
            onChange={handleCodeExecutorChange}
            required
          />
          <label htmlFor="taskInputFormat">Task Input Format:</label>
          <input
            type="text"
            id="taskInputFormat"
            name="taskInputFormat"
            value={codeExecutor.taskInputFormat}
            onChange={handleCodeExecutorChange}
            required
          />
          <label htmlFor="taskOutputFormat">Task Output Format:</label>
          <input
            type="text"
            id="taskOutputFormat"
            name="taskOutputFormat"
            value={codeExecutor.taskOutputFormat}
            onChange={handleCodeExecutorChange}
            required
          />
          <label htmlFor="taskExampleInput">Task Example Input:</label>
          <input
            type="text"
            id="taskExampleInput"
            name="input"
            value={codeExecutor.taskExample.input}
            onChange={handleTaskExampleChange}
            required
          />
          <label htmlFor="taskExampleOutput">Task Example Output:</label>
          <input
            type="text"
            id="taskExampleOutput"
            name="output"
            value={codeExecutor.taskExample.output}
            onChange={handleTaskExampleChange}
            required
          />
        </div>

        <div className="button-container">
          <button type="submit">Add Lesson</button>
        </div>
      </form>
    </div>
  );
}

export default AddLesson;
