// src/components/LessonPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Lesson from './Lesson';

const LessonPage = () => {
  const { id } = useParams(); // Используйте useParams для получения параметров маршрута
  const [lesson, setLesson] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('Fetching lesson with ID:', id);
    const fetchLesson = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/lessons/${id}`);
        setLesson(response.data);
      } catch (error) {
        setError('Ошибка при загрузке урока');
      }
    };

    fetchLesson();
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!lesson) {
    return <div>Загрузка...</div>;
  }

  return (
    <div>
      <Lesson lesson={lesson} />
    </div>
  );
};

export default LessonPage;
