import React, { useState } from 'react';
import './AddLesson.css';

function AddLesson() {
  const [title, setTitle] = useState('');
  const [sections, setSections] = useState([{ subtitle: '', content: '' }]);
  const [quizzes, setQuizzes] = useState([]);
  const [codeExecutor, setCodeExecutor] = useState({
    file: '',
    taskDescription: '',
    taskInputFormat: '',
    taskOutputFormat: '',
    taskExample: { input: '', output: '' }
  });

  const handleSectionChange = (index, event) => {
    const newSections = [...sections];
    newSections[index][event.target.name] = event.target.value;
    setSections(newSections);
  };

  const addSection = () => {
    setSections([...sections, { subtitle: '', content: '' }]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const lesson = { title, sections, quizzes, codeExecutor };

    const response = await fetch('/api/lessons', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(lesson)
    });

    if (response.ok) {
      // Обработка успешного добавления урока
      alert('Lesson added successfully!');
    } else {
      // Обработка ошибки
      alert('Error adding lesson.');
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>
        {sections.map((section, index) => (
          <div key={index} className="form-group">
            <label htmlFor={`subtitle-${index}`}>Subtitle:</label>
            <input
              type="text"
              id={`subtitle-${index}`}
              name="subtitle"
              value={section.subtitle}
              onChange={e => handleSectionChange(index, e)}
              required
            />
            <label htmlFor={`content-${index}`}>Content:</label>
            <textarea
              id={`content-${index}`}
              name="content"
              value={section.content}
              onChange={e => handleSectionChange(index, e)}
              required
            />
          </div>
        ))}
        <button type="button" className="add-section-button" onClick={addSection}>Add Section</button>

        {/* Добавьте поля для ввода данных для quizzes и codeExecutor */}
        
        <div className="button-container">
          <button type="submit">Add Lesson</button>
        </div>
      </form>
    </div>
  );
}

export default AddLesson;
