// src/components/Lesson.js
import React from 'react';
import '../styles/Lesson-style.css';
import Quiz from './Quiz';
import CodeExecutor from './CodeExecutor';
import '../styles/Quiz.css'
import '../styles/CodeEditor.css'

const Lesson = ({ lesson }) => {
  return (
    <div className='lesson-container'>
      <div className='lesson-header'>
        <h1>{lesson.title}</h1>
      </div>
      {lesson.sections.map((section, index) => (
        <div className='lesson-section' key={index}>
          {renderSection(section)}
        </div>
      ))}
      {/* {lesson.quizzes.length > 0 && (
        <div className='lesson-quizzes'>
          <h2>Тест</h2>
            <Quiz quiz={lesson.quizzes} />
        </div>
      )} */}
      {lesson.codeExecutor && (
        <div className='lesson-code-executor'>
          <CodeExecutor codeExecutor={lesson.codeExecutor} />
        </div>
      )}
    </div>
  );
};

const renderSection = (section) => {
  switch (section.type) {
    case 'header':
      return <h2>{section.content}</h2>;
    case 'paragraph':
      return <p>{section.content}</p>;
    default:
      return null;
  }
};

export default Lesson;
