import React, { useState } from 'react';
import '../styles/AddLesson.css';

const AddLesson = () => {
  const [lesson, setLesson] = useState({
    title: '',
    content: []
  });

  const handleTitleChange = (e) => {
    setLesson({ ...lesson, title: e.target.value });
  };

  const handleContentChange = (index, type, value) => {
    const newContent = [...lesson.content];
    newContent[index][type] = value;
    setLesson({ ...lesson, content: newContent });
  };

  const addContentItem = (type) => {
    let newContentItem;
    switch (type) {
      case 'text':
        newContentItem = { type, text: '' };
        break;
      case 'list':
        newContentItem = { type, list: { items: [''] } };
        break;
      case 'code':
        newContentItem = { type, code: '' };
        break;
      case 'subtitle':
        newContentItem = { type, subtitle: '' };
        break;
      case 'quizzes':
        newContentItem = { type, quizzes: [{ question: '', options: [''], answer: '' }] };
        break;
      case 'codeExecutor':
        newContentItem = { type, codeExecutor: {} };
        break;
      default:
        return;
    }
    setLesson({ ...lesson, content: [...lesson.content, newContentItem] });
  };

  const removeContentItem = (index) => {
    const newContent = [...lesson.content];
    newContent.splice(index, 1);
    setLesson({ ...lesson, content: newContent });
  };

  const handleListItemChange = (sectionIndex, itemIndex, event) => {
    const { value } = event.target;
    const newContent = [...lesson.content];
    newContent[sectionIndex].list.items[itemIndex] = value;
    setLesson({ ...lesson, content: newContent });
  };

  const addListItem = (sectionIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].list.items.push('');
    setLesson({ ...lesson, content: newContent });
  };

  const removeListItem = (sectionIndex, itemIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].list.items.splice(itemIndex, 1);
    setLesson({ ...lesson, content: newContent });
  };

  const addOption = (quizIndex) => {
    const newContent = [...lesson.content];
    newContent[quizIndex].quizzes[0].options.push('');
    setLesson({ ...lesson, content: newContent });
  };

  const removeOption = (quizIndex, optionIndex) => {
    const newContent = [...lesson.content];
    newContent[quizIndex].quizzes[0].options.splice(optionIndex, 1);
    setLesson({ ...lesson, content: newContent });
  };

  const renderSection = (section, index) => {
    switch (section.type) {
      case 'text':
        return (
          <div key={index}>
            <label>Text:</label>
            <textarea
              value={section.text}
              onChange={(e) => handleContentChange(index, 'text', e.target.value)}
            />
            <button type="button" onClick={() => removeContentItem(index)}>Удалить</button>
          </div>
        );
      case 'list':
        return (
          <div key={index}>
            <label>Список:</label>
            <ul>
              {section.list.items.map((item, itemIndex) => (
                <li key={itemIndex}>
                  <input
                    type="text"
                    value={item}
                    onChange={(e) => handleListItemChange(index, itemIndex, e)}
                  />
                  <button type="button" onClick={() => removeListItem(index, itemIndex)}>Удалить элемент</button>
                </li>
              ))}
            </ul>
            <button type="button" onClick={() => addListItem(index)}>Добавить элемент</button>
            <button type="button" onClick={() => removeContentItem(index)}>Удалить</button>
          </div>
        );
      case 'code':
        return (
          <div key={index}>
            <label>Код:</label>
            <textarea
              value={section.code}
              onChange={(e) => handleContentChange(index, 'code', e.target.value)}
            />
            <button type="button" onClick={() => removeContentItem(index)}>Удалить</button>
          </div>
        );
      case 'subtitle':
        return (
          <div key={index}>
            <label>Подзаголовок:</label>
            <input
              type="text"
              value={section.subtitle}
              onChange={(e) => handleContentChange(index, 'subtitle', e.target.value)}
            />
            <button type="button" onClick={() => removeContentItem(index)}>Удалить</button>
          </div>
        );
      case 'quizzes':
        return (
          <div key={index}>
            <label>Викторины:</label>
            <>
              {section.quizzes.map((quiz, quizIndex) => (
                <div key={quizIndex} className="form-group">
                  <label htmlFor={`question-${quizIndex}`}>Question:</label>
                  <input
                    type="text"
                    id={`question-${quizIndex}`}
                    name="question"
                    value={quiz.question}
                    onChange={(e) => handleContentChange(index, 'quizzes', e)}
                    required
                  />
                  {quiz.options.map((option, optionIndex) => (
                    <div key={optionIndex} className="option-group">
                      <label htmlFor={`option-${quizIndex}-${optionIndex}`}>Option {optionIndex + 1}:</label>
                      <input
                        type="text"
                        id={`option-${quizIndex}-${optionIndex}`}
                        name="options"
                        data-index={optionIndex}
                        value={option}
                        onChange={(e) => handleContentChange(index, 'quizzes', e)}
                        required
                      />
                      <button type="button" onClick={() => removeOption(index, optionIndex)}>
                        Remove Option
                      </button>
                    </div>
                  ))}
                  <button type="button" onClick={() => addOption(index)}>
                    Add Option
                  </button>
                  <label htmlFor={`answer-${quizIndex}`}>Answer:</label>
                  <input
                    type="text"
                    id={`answer-${quizIndex}`}
                    name="answer"
                    value={quiz.answer}
                    onChange={(e) => handleContentChange(index, 'quizzes', e)}
                    required
                  />
                </div>
              ))}
            </>
            <button type="button" onClick={() => removeContentItem(index)}>Удалить</button>
          </div>
        );
      case 'codeExecutor':
        return (
          <div key={index}>
            <label>Выполнение кода:</label>
            {/* Здесь можно добавить обработку выполнения кода */}
            <button type="button" onClick={() => removeContentItem(index)}>Удалить</button>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className='add-lesson-container'>
      <div className='lesson-header'>
        <h1>Добавить урок</h1>
        <label>Заголовок урока:</label>
        <input
          type='text'
          value={lesson.title}
          onChange={handleTitleChange}
        />
      </div>
      <div className='lesson-content'>
        {lesson.content.map((section, index) => (
          <div className='lesson-section' key={index}>
            {renderSection(section, index)}
          </div>
        ))}
      </div>
      <div className='add-buttons'>
        <button type='button' onClick={() => addContentItem('text')}>Добавить текст</button>
        <button type='button' onClick={() => addContentItem('list')}>Добавить список</button>
        <button type='button' onClick={() => addContentItem('code')}>Добавить код</button>
        <button type='button' onClick={() => addContentItem('subtitle')}>Добавить подзаголовок</button>
        <button type='button' onClick={() => addContentItem('quizzes')}>Добавить викторину</button>
        <button type='button' onClick={() => addContentItem('codeExecutor')}>Добавить выполнение кода</button>
      </div>
    </div>
  );
};

export default AddLesson;
