import React, { useState } from 'react';
import '../styles/AddLesson.css';

const AddLesson = () => {
  const [lesson, setLesson] = useState({
    title: '',
    content: []
  });

  const handleInputChange = (index, key, value) => {
    const newContent = [...lesson.content];
    newContent[index][key] = value;
    setLesson({ ...lesson, content: newContent });
  };

  const handleQuizChange = (sectionIndex, quizIndex, event) => {
    const { name, value, dataset } = event.target;
    const newContent = [...lesson.content];
    if (name === 'options') {
      const optionIndex = dataset.index;
      newContent[sectionIndex].quizzes[quizIndex].options[optionIndex] = value;
    } else {
      newContent[sectionIndex].quizzes[quizIndex][name] = value;
    }
    setLesson({ ...lesson, content: newContent });
  };

  const addOption = (sectionIndex, quizIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].quizzes[quizIndex].options.push('');
    setLesson({ ...lesson, content: newContent });
  };

  const removeOption = (sectionIndex, quizIndex, optionIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].quizzes[quizIndex].options.splice(optionIndex, 1);
    setLesson({ ...lesson, content: newContent });
  };

  const addQuiz = (sectionIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].quizzes.push({ question: '', options: [''], answer: '' });
    setLesson({ ...lesson, content: newContent });
  };

  const removeQuiz = (sectionIndex, quizIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].quizzes.splice(quizIndex, 1);
    setLesson({ ...lesson, content: newContent });
  };

  const addContentItem = (type) => {
    let newContentItem;
    switch (type) {
      case 'text':
        newContentItem = { type, text: '' };
        break;
      case 'list':
        newContentItem = { type, list: { items: [''] } };
        break;
      case 'code':
        newContentItem = { type, code: '' };
        break;
      case 'subtitle':
        newContentItem = { type, subtitle: '' };
        break;
      case 'quizzes':
        newContentItem = { type, quizzes: [{ question: '', options: [''], answer: '' }] };
        break;
      case 'codeExecutor':
        newContentItem = { 
          type, 
          codeExecutor: { 
            test: [{ input: '', expectedOutput: '' }], 
            taskDescription: '', 
            taskInputFormat: '', 
            taskOutputFormat: '', 
            taskExample: { input: '', output: '' } 
          } 
        };
        break;
      default:
        return;
    }
    setLesson({ ...lesson, content: [...lesson.content, newContentItem] });
  };

  const handleCodeExecutorChange = (sectionIndex, key, value) => {
    const newContent = [...lesson.content];
    const keys = key.split('.');
    let current = newContent[sectionIndex].codeExecutor;

    keys.forEach((k, i) => {
      if (i === keys.length - 1) {
        current[k] = value;
      } else {
        current = current[k];
      }
    });

    setLesson({ ...lesson, content: newContent });
  };

  const addCodeExecutorTest = (sectionIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].codeExecutor.test.push({ input: '', expectedOutput: '' });
    setLesson({ ...lesson, content: newContent });
  };

  const removeCodeExecutorTest = (sectionIndex, testIndex) => {
    const newContent = [...lesson.content];
    newContent[sectionIndex].codeExecutor.test.splice(testIndex, 1);
    setLesson({ ...lesson, content: newContent });
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/lessons', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(lesson),
      });
      // Обработка успешного сохранения
    } catch (error) {
      console.error('Ошибка при сохранении урока:', error);
      // Обработка ошибки сохранения
    }
  };

  return (
    <div className="add-lesson-container">
      <h2>Добавление урока</h2>
      <label>Заголовок урока:</label>
      <input
        type="text"
        value={lesson.title}
        onChange={(e) => setLesson({ ...lesson, title: e.target.value })}
      />
      <button type="button" onClick={() => addContentItem('quizzes')}>
        Добавить викторину
      </button>
      {lesson.content.map((section, sectionIndex) => (
        <div key={sectionIndex} className="section-container">
          {section.type === 'quizzes' && (
            <div>
              <label>Викторины:</label>
              {section.quizzes.map((quiz, quizIndex) => (
                <div key={quizIndex} className="quiz-container">
                  <label>Вопрос:</label>
                  <input
                    type="text"
                    value={quiz.question}
                    onChange={(e) => handleQuizChange(sectionIndex, quizIndex, e)}
                    name="question"
                  />
                  <label>Варианты ответа:</label>
                  {quiz.options.map((option, optionIndex) => (
                    <div key={optionIndex}>
                      <input
                        type="text"
                        value={option}
                        onChange={(e) => handleQuizChange(sectionIndex, quizIndex, e)}
                        name="options"
                        data-index={optionIndex}
                      />
                      <button type="button" onClick={() => removeOption(sectionIndex, quizIndex, optionIndex)}>
                        Удалить вариант
                      </button>
                    </div>
                  ))}
                  <button type="button" onClick={() => addOption(sectionIndex, quizIndex)}>
                    Добавить вариант
                  </button>
                  <label>Правильный ответ:</label>
                  <input
                    type="text"
                    value={quiz.answer}
                    onChange={(e) => handleQuizChange(sectionIndex, quizIndex, e)}
                    name="answer"
                  />
                  <button type="button" onClick={() => removeQuiz(sectionIndex, quizIndex)}>
                    Удалить вопрос
                  </button>
                </div>
              ))}
              <button type="button" onClick={() => addQuiz(sectionIndex)}>
                Добавить вопрос
              </button>
            </div>
          )}
          {section.type === 'codeExecutor' && (
            <div>
              <label>CodeExecutor:</label>
              <div className="form-group">
                <label>Описание задачи:</label>
                <textarea
                  value={section.codeExecutor.taskDescription}
                  onChange={(e) => handleCodeExecutorChange(sectionIndex, 'taskDescription', e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Формат ввода:</label>
                <textarea
                  value={section.codeExecutor.taskInputFormat}
                  onChange={(e) => handleCodeExecutorChange(sectionIndex, 'taskInputFormat', e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Формат вывода:</label>
                <textarea
                  value={section.codeExecutor.taskOutputFormat}
                  onChange={(e) => handleCodeExecutorChange(sectionIndex, 'taskOutputFormat', e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Пример:</label>
                <div>
                  <label>Ввод:</label>
                  <textarea
                    value={section.codeExecutor.taskExample.input}
                    onChange={(e) => handleCodeExecutorChange(sectionIndex, 'taskExample.input', e.target.value)}
                  />
                </div>
                <div>
                  <label>Вывод:</label>
                  <textarea
                    value={section.codeExecutor.taskExample.output}
                    onChange={(e) => handleCodeExecutorChange(sectionIndex, 'taskExample.output', e.target.value)}
                  />
                </div>
              </div>
              <div className="form-group">
                <label>Тесты:</label>
                {section.codeExecutor.test.map((test, testIndex) => (
                  <div key={testIndex} className="test-group">
                    <div>
                      <label>Ввод:</label>
                      <input
                        type="text"
                        value={test.input}
                        onChange={(e) => handleCodeExecutorChange(sectionIndex, `test.${testIndex}.input`, e.target.value)}
                      />
                    </div>
                    <div>
                      <label>Ожидаемый вывод:</label>
                      <input
                        type="text"
                        value={test.expectedOutput}
                        onChange={(e) => handleCodeExecutorChange(sectionIndex, `test.${testIndex}.expectedOutput`, e.target.value)}
                      />
                    </div>
                    <button type="button" onClick={() => removeCodeExecutorTest(sectionIndex, testIndex)}>
                      Удалить тест
                    </button>
                  </div>
                ))}
                <button type="button" onClick={() => addCodeExecutorTest(sectionIndex)}>
                  Добавить тест
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
      <button type="button" onClick={handleSubmit}>
        Отправить
      </button>
    </div>
  );
};

export default AddLesson;
