import React, { useState } from 'react';
import axios from 'axios';
import CodeEditor from './CodeEditor';

const CodeExecutor = (file) => {
  const [code, setCode] = useState('');
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const [error, setError] = useState('');
  const [resultMessage, setResultMessage] = useState('');
  const [input_data, setInput_data] = useState('');
  const [expectedOutput, setExpectedOutput] = useState('');



  const executeCode = async () => {
    try {
      const response = await axios.post('http://localhost:5000/execute', { code, input, fileToCompare: file });
      setOutput(response.data.output);
      setError(response.data.error);
      setResultMessage(response.data.resultMessage);
      setInput_data(response.data.inputData);
      setExpectedOutput(response.data.expectedOutput);
    } catch (err) {
      setError('Error executing code.');
    }
  };

  return (
    <div>
      <CodeEditor code={code} setCode={setCode} input={input} setInput={setInput} />
      <button onClick={executeCode} style={{ marginTop: '20px' }}>Запустить код</button>
      <button onClick={executeCode} style={{ margin: '20px' }}>Отправить на проверку</button>
      <div>
        <h2>{resultMessage}</h2>
        {error && (
            <div>
                <h3>Ошибка:</h3>
                <pre>{error}</pre>
            </div>
        )}
        <h3>Ваш вывод:</h3>
        <pre>{output}</pre>
        <h3>Ожидаемый вывод:</h3>
        <pre>{expectedOutput}</pre>
        <h3>Входные данные:</h3>
        <pre>{input_data}</pre>
      </div>
    </div>
  );
}

export default CodeExecutor;
